hook:
// key-value store: save a value associated with a key
if (!res.locals.context.kvs) res.locals.context.kvs = {};
res.locals.context.kvs[req.params.key] = req.body;
res.send("saved");

// key-value store: fetch the value associated with a key
var key = req.params.key;
var store = res.locals.context.kvs;
if (key in store) res.send(store[key]);
else res.status(404).send('not found');

// key-value store: delete the value at a key
delete res.locals.context.kvs[req.params.key];
res.send('deleted');
